import axios from 'axios';
import React, {useEffect, useRef, useState} from 'react';

const QuizResult = ({topic, score, quizID, total, attempts, onTryAgain, difficulty, onShowExplanations, navigate, quizData, userAnswers}) => {
    const isLogin = localStorage.getItem("isLogin") === "true";
    const userID = localStorage.getItem("userID");
    const [explanationID, setExplanationID] = useState(null);
    //const [hasSaved, setHasSaved] = useState(false);

    const hasAttemptedSave = useRef(false);
    const hasSavedExplanations = useRef(false);

    // Save explanations
    useEffect(() => {
        const saveExplanations = async () => {
          if (!userID || !quizData.length || hasSavedExplanations.current) return;

          try {
            // Create explanations data with all questions, answers, etc.
            const explanationData = quizData.map((q, index) => ({
              question: q.question,
              options: q.options,
              correctAnswer: q.correctAnswer,
              userAnswer: userAnswers[index],
              explanation: q.explanation,
            }));

            // Simple payload with just userID and all explanations as a single JSON object
            const payload = {
              userID,
              explanationData
            };

            const response = await axios.post('https://localhost:3001/api/explanations/save', payload);
            const explanationID = Integer(response.data.explanationID);
            setExplanationID(explanationID);

            localStorage.setItem("explanationID", String(response.data.explanationID));
            console.log("Explanation saved with ID:", explanationID);


          } catch (error) {
            console.error("Failed to save explanations:", error);
          }
        };

        if (isLogin && !hasAttemptedSave.current) {
            saveExplanations();
        }
    }, [userID, quizData, userAnswers, isLogin]);

    // Save quiz result
    useEffect(() => {
        const saveResults = async () => {
        try {
            const payload = {
            userID,
            topic,
            score,
            total,
            attempts,
            difficulty,
            quizID,
            explanationID,
            };
            console.log("INSIDE Quiz Result quizID: ", quizID);

            const res = await axios.post("https://localhost:3001/api/result/save-results", payload);
            if (res.status === 200) {
            // Successfully saved
                console.log("Saved results successfully");
            }
        } catch (err) {
            console.error("Error saving quiz:", err);
            alert("Failed to save quiz.");
        }
        };

        if (isLogin && !hasAttemptedSave.current) {
            hasAttemptedSave.current = true;
            saveResults();
        }
    }, [isLogin, userID, topic, score, total, attempts, difficulty, quizID, explanationID]);



    return (
        <div className="card text-white border" style={{backgroundColor: "#1a1a1a",}} >
            <div className="card-body text-center">
                <h2>Quiz Results</h2>
                <h3>Your Score: {score} out of {total}</h3>
                <p>You got {Math.round((score / total) * 100)}%</p>


                <div className="mt-4">
                    {isLogin && 
                        <>
                            <p>You've attempted this quiz {attempts} times. Would you like to see the explanations or try again?</p>
                            <button className="btn btn-info me-2" onClick={onShowExplanations}>
                                Show Explanations
                            </button>
                        </>
                    }
                    <button className="btn btn-primary me-2" onClick={onTryAgain}>
                        Try Again
                    </button>
                    <button className="btn btn-secondary me-2" onClick={() => navigate('/quiz-generator/ai')}>
                        New Quiz
                    </button>
                    <button className="btn me-2 btn-danger" onClick={() => navigate('/')}>
                        Exit
                    </button>
                </div>
            </div>
        </div>
    );
}

export default QuizResult;
