// pages/TakeQuiz.js
// pages/TakeQuiz.js
import React, { useState, useEffect, useRef } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import QuestionCard from '../components/QuestionCard';
import QuizResult from '../components/QuizResult';
import QuizExplanations from '../components/QuizExplanations';
import axios from 'axios';

const TakeQuiz = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const [quizData, setQuizData] = useState([]);
    const [quizTopic, setQuizTopic] = useState('');
    const [quizDifficulty, setQuizDifficulty] = useState('');
    const [quizID, setQuizID] = useState('');
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [selectedAnswer, setSelectedAnswer] = useState('');
    const [score, setScore] = useState(0);
    const [userAnswers, setUserAnswers] = useState([]);
    const [showResults, setShowResults] = useState(false);
    const [showExplanations, setShowExplanations] = useState(false);
    const [totalAttempts, setTotalAttempts] = useState(0);
    const [quizType, setQuizType] = useState('');
    const [hasSaved, setHasSaved] = useState(false);

    // Don't use state for tracking saved status - use refs exclusively
    const saveInitiated = useRef(false);
    const quizSavedRef = useRef(false);

    const isLogin = localStorage.getItem("isLogin") === "true";
    const userID = localStorage.getItem("userID");

    // Load quiz data on component mount
    useEffect(() => {
        if (location.state?.questions) {
            setQuizData(location.state.questions);
            setQuizTopic(location.state.topic || 'Quiz');
            setQuizDifficulty(location.state.difficulty);
            setQuizID(location.state.quizID);
            setQuizType(location.state.type);
            setUserAnswers(new Array(location.state.questions.length).fill(''));

            // Reset saving flags on new quiz load
            saveInitiated.current = false;
            quizSavedRef.current = false;
        } else {
            navigate('/quiz-generator/ai');
        }
    }, [location, navigate]);

    // Save quiz results function
    const saveQuizResults = async () => {
        if (!isLogin || saveInitiated.current || !userID || quizSavedRef.current || !quizData.length
        ) {
            console.log("Skipping save operation:", {
                saveInitiated: saveInitiated.current,
                isLogin,
                userID: !!userID,
                quizSaved: quizSavedRef.current,
                quizDataLength: quizData.length
            });
            return;
        }

        // Mark as initiated immediately
        saveInitiated.current = true;
        console.log("⚡ Save initiated");

        try {
            console.log("Saving quiz result...");

            // Step 1: Save explanations
            const explanationResponse = await axios.post('https://localhost:3001/api/explanations/save', {
                userID,
                quizID,
                topic: quizTopic,
                explanationData: {
                    quizData,
                    userAnswers
                }
            });

            const explanationID = explanationResponse.data.explanationID;
            console.log("Explanation saved with ID:", explanationID);

            // Step 2: Save quiz results
            const resultPayload = {
                userID,
                topic: quizTopic,
                score,
                total: quizData.length,
                attempts: totalAttempts,
                difficulty: quizDifficulty,
                quizID,
                explanationID,
            };

            const resultRes = await axios.post("https://localhost:3001/api/result/save-results", resultPayload);
            if (resultRes.status === 200) {
                console.log("Saved results successfully");
                quizSavedRef.current = true;
            }
        } catch (err) {
            console.error("Failed to save quiz result or explanations:", err);
        }
    };

    const handleNextQuestion = () => {
        // Save the user's answer for the current question
        const newUserAnswers = [...userAnswers];
        newUserAnswers[currentQuestion] = selectedAnswer;
        setUserAnswers(newUserAnswers);

        // Check if the answer is correct and update score
        if (selectedAnswer === quizData[currentQuestion].correctAnswer) {
            setScore(score + 1);
        }

        // Move to next question or show results
        if (currentQuestion < quizData.length - 1) {
            setCurrentQuestion(currentQuestion + 1);
            setSelectedAnswer('');
        } else {
            setShowResults(true);
            // Increment total attempts when quiz is completed
            setTotalAttempts(totalAttempts + 1);

            // Call save directly here when quiz completes
            saveQuizResults();
        }
    };

    const handleRetakeQuiz = () => {
        setCurrentQuestion(0);
        setSelectedAnswer('');
        setScore(0);
        // Clear user answers
        setUserAnswers(new Array(quizData.length).fill(''));
        // Reset to first question
        setShowResults(false);
        setShowExplanations(false);

        // Reset saved state when retaking quiz
        saveInitiated.current = false;
        quizSavedRef.current = false;
    };

    const handleContinueTrying = () => {
        // Reset to first question for another attempt
        setCurrentQuestion(0);
        setSelectedAnswer('');
        setScore(0);
        setShowResults(false);
        // Keep user's previous answers for reference
    };

    // Go back to results without remounting QuizResult
    const handleBackToResults = () => {
        setHasSaved("true")
        setShowExplanations(false);
    };

    if (!quizData.length) return <div className="container mt-5 text-center">Loading...</div>;

    return (
        <div className="container text-white mt-4 mb-4 p-4 border-0 rounded-4" style={{backgroundColor: "#1a1a1a"}}>
            <h1 className="text-center mb-4">{quizTopic} Quiz</h1>
            {!showResults ? (
                <QuestionCard
                    question={quizData[currentQuestion]}
                    current={currentQuestion}
                    total={quizData.length}
                    selectedAnswer={selectedAnswer}
                    onSelect={setSelectedAnswer}
                    onNext={handleNextQuestion}
                />
            ) : !showExplanations ? (
                <QuizResult
                    topic={quizTopic}
                    score={score}
                    quizID={quizID}
                    total={quizData.length}
                    attempts={totalAttempts}
                    onTryAgain={handleContinueTrying}
                    difficulty={quizDifficulty}
                    onShowExplanations={() => setShowExplanations(true)}
                    navigate={navigate}
                    quizData={quizData}
                    userAnswers={userAnswers}
                    quizType={quizType}
                    // Remove all saving logic from QuizResult
                />
            ) : (
                <QuizExplanations
                    quizData={quizData}
                    userAnswers={userAnswers}
                    onRetake={handleRetakeQuiz}
                    onBackToResults={handleBackToResults}
                    navigate={navigate}
                    readOnly={false}
                    quizType={quizType}
                    quizID={quizID}
                />
            )}
        </div>
    );
}

export default TakeQuiz;
