import React from "react";
import "../styles/AuthForm.css"; 
import { GoogleLogin } from "@react-oauth/google";

const AuthForm = ({ 
    onSubmit, isLogin, 
    email, setEmail, 
    password, setPassword, 
    setUserName, userName, 
    showGoogleButton, 
    onGoogleSuccess, 
    onGoogleError }) => {
    return (
        <div className="form-wrapper">
            <div className="form-container">
                <form onSubmit={onSubmit} className="bg-gray-3 p-4 rounded-lg shadow-md">
                    <h1 className="text-white text-center mb-3">{isLogin ? "Login" : "Create Account"}</h1>
                    {!isLogin && (
                        <div className="form-group">
                            <label htmlFor="userName" >User Name:</label>
                            <input
                                type="text"
                                id="userName"
                                value={userName}
                                onChange={(e) => setUserName(e.target.value)}
                                required
                                className="form-control bg-black text-gray border-bottom-0 border-white"
                                style={{
                                boxShadow: 'none',
                                WebkitBoxShadow: '0 0 0 1000px black inset',
                                WebkitTextFillColor: 'white',
                                }}
                            />
                        </div>
                    )}
                    <div className="form-group">
                        <label htmlFor="email" >Email:</label>
                        <input
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                            className="form-control bg-black text-white border-bottom-0 border-white"
                            style={{
                            boxShadow: 'none',
                            WebkitBoxShadow: '0 0 0 1000px black inset',
                            WebkitTextFillColor: 'white',
                            }}
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password" >Password:</label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                            className="form-control bg-black text-white border-bottom-0 border-white"
                            style={{
                            boxShadow: 'none',
                            WebkitBoxShadow: '0 0 0 1000px black inset',
                            WebkitTextFillColor: 'white',
                            }}
                        />
                    </div>
                    <button type="submit" className={`btn-signup ${isLogin ? "btn-primary" : "btn-success"} btn-block`}>
                        {isLogin ? "Login" : "Sign Up"}
                    </button>
                </form>
                {showGoogleButton && (
                    <div className="google-login-wrapper my-3 text-center">
                        <GoogleLogin
                            onSuccess={onGoogleSuccess}
                            onError={onGoogleError}
                        />
                    </div>
                )}
            </div>
        </div>
    )
}

export default AuthForm;
