// src/pages/AttemptPage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const AttemptPage = () => {
  const { quizID } = useParams();
  const userID = localStorage.getItem("userID");
  const [attempts, setAttempts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAttempts = async () => {
      try {
        const res = await axios.get(`/api/result/attempts/${userID}/${quizID}`);
        setAttempts(res.data);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching attempts:", err);
        setError("Failed to load attempts.");
        setLoading(false);
      }
    };

    fetchAttempts();
  }, [quizID, userID]);

  if (loading) return <p className="text-white">Loading attempts...</p>;
  if (error) return <p className="text-danger">{error}</p>;

  return (
    <div className="container pt-4 pb-4">
      <div className="card text-bg-secondary rounded-4 shadow-sm p-4" style={{ backgroundColor: '#1a1a1a' }}>
        <h2 className="mb-4">Past Attempts</h2>
        {attempts.length === 0 ? (
          <p>No attempts found for this quiz.</p>
        ) : (
          <div className="list-group">
            {attempts.map((attempt, index) => (
              <div key={index} className="list-group-item list-group-item-dark bg-dark text-white mb-2 rounded">
                <div className="d-flex justify-content-between">
                  <strong>Attempt #{index + 1}</strong>
                  <span className="badge bg-primary">
                    {attempt.score}/{attempt.totalQuestions} ({Math.round((attempt.score / attempt.totalQuestions) * 100)}%)
                  </span>
                </div>
                <small>Taken on {new Date(attempt.createdAt).toLocaleString()}</small>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default AttemptPage;
