// Layout.js
import React from "react";
import { Outlet, useLocation } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';
import HomeNavbar from "./HomeNavbar";
import QuizNavbar from "./QuizNavbar";
import Footer from "./Footer";

const Layout = ({ isLogin, setIsLogin }) => {
    const location = useLocation();

    let Navbar;
    if (
      location.pathname === "/" ||
      location.pathname === "/ai-quiz" ||
      location.pathname === "/login" ||
      location.pathname === "/signup"
    ) {
      Navbar = () => <HomeNavbar isLogin={isLogin} setIsLogin={setIsLogin} />;
    } else if (location.pathname.startsWith("/quiz")) {
      Navbar = QuizNavbar;
    } else {
      Navbar = null;
    }

    return (
      <div className="App bg-black d-flex flex-column min-vh-100">
        {Navbar && <Navbar />}
        <main><Outlet /></main>
        <div className="flex-grow-1"></div>
        <Footer />
      </div>
    );
  };

  export default Layout;
