// HomeNavbar.jsx
import "../styles/HomeNavbar.css"
import React from "react";
import { Link, useLocation } from "react-router-dom";
import logo from "../assets/quizapp_logo.png";
import icon from "../assets/person-icon-1.png";

// HomeNavbar component
const HomeNavbar = () => {
    const location = useLocation();

    // Determine which link to show
    const isLoginPage = location.pathname === "/login";
    const isSignupPage = location.pathname === "/signup";
    const isLoggedIn = localStorage.getItem === "true";

    return (
        <nav className="navbar bg-dark">
            <div className="logo">
                <Link to="/">
                    <img src={logo} alt="quiz-app-logo" />
                </Link>
            </div>
            <div className="nav-right">
            <ul className="nav-links">
                {!isLoggedIn && !isLoginPage && (
                    <li><Link to="/login" className="bordered-link">Login</Link></li>
                )}
                {!isLoggedIn && !isSignupPage && (
                    <li><Link to="/signup" className="bordered-link">Sign Up</Link></li>
                )}
                {isLoggedIn && (
                    <>
                        <li><Link to="/profile" className="bordered-link">Profile</Link></li>
                        <li>
                            <button
                            onClick={() => {
                                localStorage.removeItem("isLogin");
                                window.location.href = "/"; // or navigate("/")
                            }}
                            className="bordered-link"
                            >
                            Logout
                            </button>
                        </li>
                    </>
                  )}
                </ul>
                <div className="icon">
                    <Link to="/">
                        <img src={icon} alt="quiz-app-logo" />
                    </Link>
                </div>
            </div>
        </nav>
    );
};

export default HomeNavbar;
