// src/pages/AttemptPage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const AttemptPage = () => {
  const { quizID } = useParams();
  const userID = localStorage.getItem("userID");
  const [attempts, setAttempts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAttempts = async () => {
      try {
        const res = await axios.get(`/api/result/attempts/${userID}/${quizID}`);
        setAttempts(res.data);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching attempts:", err);
        setError("Failed to load attempts.");
        setLoading(false);
      }
    };

    /*const handleDelete = async (attemptID) => {
        if (!window.confirm("Are you sure you want to delete this attempt?")) return;

        try {
          await axios.delete(`/api/attempts/${attemptID}`);
          setAttempts((prev) => prev.filter((a) => a.attemptID !== attemptID));
        } catch (err) {
          console.error("Failed to delete attempt:", err);
          alert("Something went wrong.");
        }
    };*/


    fetchAttempts();
  }, [quizID, userID]);

  if (loading) return <p className="text-white">Loading attempts...</p>;
  if (error) return <p className="text-danger">{error}</p>;

  return (
    <div className="container pt-4 pb-4">
      <div className="card text-white rounded-4 shadow-sm p-4" style={{ backgroundColor: '#1a1a1a' }}>
        <h2 className="mb-4">Past Attempts</h2>
        {attempts.map((attempt, index) => (
            <div
                key={index}
                className="list-group-item list-group-item-dark bg-dark text-white mb-2 rounded"
            >
                <div className="d-flex justify-content-between align-items-center">
                    <div className='pl-5'>
                        <strong>Attempt #{index + 1}</strong>
                        <div>
                        <small>
                            Taken on {new Date(attempt.createdAt).toLocaleString()}
                        </small>
                        </div>
                    </div>
                    <div className="d-flex align-items-center gap-3">
                        <span className="badge bg-primary">
                        {attempt.score}/{attempt.totalQuestions} (
                        {Math.round((attempt.score / attempt.totalQuestions) * 100)}%)
                        </span>
                        <i
                        className="bi bi-trash text-danger"
                        style={{ cursor: 'pointer' }}
                        /*onClick={() => handleDelete(attempt.attemptID)} // replace with your actual ID*/
                        ></i>
                    </div>
                </div>
            </div>
        ))}
      </div>
    </div>
  );
};

export default AttemptPage;
