// pages/GenerateQuiz.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useParams } from 'react-router-dom';

const QuizGenerator = () => {

  const { type } = useParams();
  const quizType = type;

  const [topic, setTopic] = useState('');
  const [numberOfQuestions, setNumberOfQuestions] = useState(5);
  const [difficulty, setDifficulty] = useState('medium');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [quizData, setQuizData] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const saveQuizAndNavigate = async () => {
      if (quizData && quizData.length > 0) {
        try {
          // Create the quiz
          const response = await axios.post('https://localhost:3001/api/quiz/create', {
            title: topic,
          });

          const quizID = response.data.quizID;
          console.log("Quiz created ID: ", quizID);

          // Save all questions
          await axios.post('https://localhost:3001/api/quiz/save-questions', {
            quizID: quizID,
            title: topic,
            questions: quizData.map(q => ({
              question: q.question,
              answer: q.correctAnswer,
              options: q.options,
              difficulty: difficulty
            }))
          });

          navigate('/take-quiz', {
            state: {
              questions: quizData,
              topic,
              difficulty,
              quizID,
            },
          });
        } catch (error) {
          console.error("Failed to save quiz:", error);
          alert("There was an error saving the quiz. Please try again.");
        }
      }
    };

    saveQuizAndNavigate();
  }, [quizData, navigate, topic, difficulty]);


  const handleGenerateQuiz = async (e) => {
    e.preventDefault();

    setIsLoading(true);
    setError(null);

    try {
      let response;

      // AI quiz generator
      if (quizType === 'ai') {
        response = await axios.post('https://localhost:3001/api/ai-quiz/generate', {
          topic,
          numberOfQuestions,
          difficulty
        });

        if (response.data.success) {
          setQuizData(response.data.questions);
        } else {
          setError('Failed to generate quiz questions.');
        }

        // Trivia quiz generator
      } else {
        response = await axios.post('https://localhost:3001/api/trivia', {
          topic,
          numberOfQuestions,
          difficulty
        });
      }


    } catch (err) {
      setError('Error connecting to server. Please try again later.');
      console.error('Error generating quiz:', err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container text-white mt-4 mb-4 p-4 rounded" style={{backgroundColor: "#1a1a1a"}}>
      <div className="form-wrapper flex-column">
        <h2 className="text-white text-center mb-3">Ready to Challenge Yourself?</h2>
        <p className="text-white text-center mb-0">
          Choose a topic, set your difficulty, and weâ€™ll generate questions just for you!
        </p>
          <div className="form-container">
            <form onSubmit={handleGenerateQuiz} className="bg-gray-3 p-3 rounded-lg m-4 shadow-md" >
              <div className="form-group">
                <label htmlFor="topic">What do you want to get tested on?</label>
                <input
                  type="text"
                  id="topic"
                  value={topic}
                  onChange={(e) => setTopic(e.target.value)}
                  placeholder="e.g., Artificial Intelligence"
                  required
                  className="form-control bg-black text-white border-bottom-0 border-white"
                  style={{
                    boxShadow: 'none',
                    WebkitBoxShadow: '0 0 0 1000px black inset',
                    WebkitTextFillColor: 'white',
                  }}
                />
              </div>

              <div className="form-group">
                <label htmlFor="numQuestions">Number of Questions:</label>
                <select 
                  id="numQuestions" 
                  value={numberOfQuestions}
                  onChange={(e) => setNumberOfQuestions(Number(e.target.value))}
                  className="form-control bg-black text-white border-bottom-0"
                >
                  <option value="3">3</option>
                  <option value="5">5</option>
                  <option value="10">10</option>
                </select>
              </div>

              <div className="form-group">
                <label htmlFor="difficulty">Difficulty Level:</label>
                <select 
                  id="difficulty"
                  value={difficulty}
                  onChange={(e) => setDifficulty(e.target.value)}
                  className="form-control bg-black text-white border-bottom-0"
                >
                  <option value="easy">Easy</option>
                  <option value="medium">Medium</option>
                  <option value="hard">Hard</option>
                </select>
              </div>

              <button 
                type="submit"
                disabled={isLoading}
                className="btn btn-primary transition"
                style={{transformOrigin: 'center'}}
                onMouseOver={ (e) =>  e.target.style.transform = 'scale(1.05)'}
                onMouseOut={(e) =>  e.target.style.transform = 'scale(1)'}
              >
                {isLoading ? 'Generating...' : 'Generate Quiz'}
              </button>
            </form>
            {error && <div className="alert alert-danger mt-3">{error}</div>}
          </div>
      </div>
    </div>
  );
}

export default QuizGenerator;
