import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';


const MyQuizzes = () => {
    const [quizzes, setQuizzes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const userID = localStorage.getItem("userID");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchQuizzes = async () => {
      try {
        const res = await axios.get(`/api/quiz/user/${userID}`);
        setQuizzes(res.data);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching quizzes:", err);
        setError("Failed to load quizzes.");
        setLoading(false);
      }
    };

    fetchQuizzes();
  }, [userID]);

  if (loading) return <p className="text-white">Loading your quizzes...</p>;
  if (error) return <p className="text-danger">{error}</p>;

  return (
    <div className="container mt-4 text-white">
      <h2 className="mb-3">My Quizzes</h2>
      {quizzes.length === 0 ? (
        <p>You haven't saved any quizzes yet.</p>
      ) : (
        <ul className="list-group">
          {quizzes.map((quiz) => (
            <li
              key={quiz.quizID}
              className="list-group-item list-group-item-dark d-flex justify-content-between align-items-center"
              onClick={() => navigate(`/quiz/${quiz.quizID}`)}
              style={{ cursor: 'pointer' }}
            >
              <div>
                <strong>{quiz.topic}</strong>
                <div className="small text-muted">
                  {new Date(quiz.createdAt).toLocaleDateString()}
                </div>
              </div>
              <span className="badge bg-primary rounded-pill">
                {quiz.score}/{quiz.totalQuestions}
              </span>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default MyQuizzes;
