// src/pages/Explanation.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import QuizExplanations from '../components/QuizExplanations';

const Explanation = () => {
  const { quizID, explanationID } = useParams();
  const navigate = useNavigate();
  const [quizData, setQuizData] = useState([]);
  const [userAnswers, setUserAnswers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchExplanationData = async () => {
      try {
        const res = await axios.get(`/api/explanations/${explanationID}`);
        setQuizData(res.data.questions); // assuming response has questions array
        setUserAnswers(res.data.userAnswers); // assuming it's returned too
        setLoading(false);
      } catch (err) {
        console.error("Error fetching explanation data:", err);
        setLoading(false);
      }
    };

    fetchExplanationData();
  }, [quizID, explanationID]);

  const handleRetake = () => {
    navigate(`/quiz/${quizID}`); // or wherever your retake route is
  };

  if (loading) return <p className="text-white">Loading explanations...</p>;
  if (!quizData.length) return <p className="text-danger">No explanations found.</p>;

  return (
    <div className="container pt-4 pb-4">
      <QuizExplanations
        quizData={quizData}
        userAnswers={userAnswers}
        onRetake={handleRetake}
        navigate={navigate}
      />
    </div>
  );
};

export default Explanation;
