// src/components/RetakeQuiz.js
import { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const RetakeQuiz = () => {
    const { quizID } = useParams();
    const [topic, setTopic] = useState('');
    const [questions, setQuestions] = useState([]);
    const [difficulty, setDifficulty] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await axios.get(`/api/quiz/questions/${quizID}`);
                setQuestions(response.data.questions.questions); // Adjust depending on your API response shape
                setTopic(response.data.questions.title);
                setDifficulty(response.data.questions.difficulty);
                setLoading(false);

                navigate('/take-quiz', {
                    state: {
                      questions,
                      topic,
                      difficulty,
                      quizID,
                    },
                });
            } catch (error) {
                setError('Failed to fetch quiz questions');
                setLoading(false);
            }
        };

        fetchQuestions();
    }, [quizID]);
};

export default RetakeQuiz;
