import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Loader2, History, RefreshCw } from 'lucide-react';

const AttemptPage = () => {
  const { quizID } = useParams();
  const navigate = useNavigate();
  const [attempts, setAttempts] = useState([]);
  const [loading, setLoading] = useState(true);
  const userID = localStorage.getItem('userID');

  useEffect(() => {
    const fetchAttempts = async () => {
      try {
        const res = await axios.get(`/api/result/attempts/${userID}/${quizID}`);
        setAttempts(res.data);
      } catch (err) {
        console.error('Error fetching attempts:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchAttempts();
  }, [userID, quizID]);

  if (loading) {
    return (
      <div className="text-white flex justify-center items-center h-64">
        <Loader2 className="animate-spin mr-2" /> Loading attempts...
      </div>
    );
  }

  if (!attempts.length) {
    return (
      <div className="text-center text-white pt-10">
        <History className="mx-auto mb-2" size={32} />
        <p>No attempts found for this quiz yet.</p>
      </div>
    );
  }

  return (
    <div className="container pt-6 pb-10">
      <h2 className="text-white text-2xl mb-4">Previous Attempts</h2>
      <div className="grid md:grid-cols-2 gap-4">
        {attempts.map((attempt, index) => (
          <Card key={index} className="bg-[#1f1f1f] text-white p-4 rounded-2xl shadow-md">
            <div className="flex justify-between items-center mb-2">
              <h4 className="text-lg font-semibold">Attempt #{index + 1}</h4>
              <Badge className="bg-success">{attempt.score}/{attempt.totalQuestions}</Badge>
            </div>
            <p className="text-sm mb-1">Date: {new Date(attempt.createdAt).toLocaleDateString()}</p>
            <p className="text-sm mb-3">Accuracy: {Math.round((attempt.score / attempt.totalQuestions) * 100)}%</p>
            <button
              onClick={() => navigate(`/explanations/${attempt.explanationID}`)}
              className="inline-flex items-center bg-primary text-white py-1 px-3 rounded hover:bg-opacity-90 transition"
            >
              <RefreshCw size={16} className="mr-1" /> View Explanation
            </button>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default AttemptPage;
