// pages/QuizResult.js
import axios from 'axios';
import React, {useEffect, useRef, useState} from 'react';

const QuizResult = ({topic, score, quizID, total, attempts, onTryAgain, difficulty, onShowExplanations, navigate, quizData, userAnswers, quizType}) => {
    const isLogin = localStorage.getItem("isLogin") === "true";
    const userID = localStorage.getItem("userID");

    //const [explanationID, setExplanationID] = useState(null);
    //const [hasSaved, setHasSaved] = useState(false);
    const type = quizType;
    console.log("Quiz Type: ", type);

    const [hasSaved, setHasSaved] = useState(false);
    const isInitialMount = useRef(true);

    const hasAttemptedSave = useRef(false);
    console.log("HAS SAVED: ", hasSaved);

    //const hasSavedExplanations = useRef(false);

    // Save explanations
    useEffect(() => {
      if (isInitialMount.current) {
        isInitialMount.current = false;
        return;
      }
      if (!isLogin || !userID || !quizData.length || hasSaved || hasAttemptedSave.current) {
          return; // ❌ don't even define or call saveEverything if not needed
      }

      const saveEverything = async () => {
          console.log("Saving quiz result...");
          hasAttemptedSave.current = true;

          try {
              // Step 1: Save explanations
              const explanationResponse = await axios.post('https://localhost:3001/api/explanations/save', {
                  userID,
                  quizID,
                  topic,
                  explanationData: {
                      quizData,
                      userAnswers
                  }
              });

              const explanationID = explanationResponse.data.explanationID;
              console.log("Explanation saved with ID:", explanationID);

              // Step 2: Save quiz results
              const resultPayload = {
                  userID,
                  topic,
                  score,
                  total,
                  attempts,
                  difficulty,
                  quizID,
                  explanationID,
              };

              const resultRes = await axios.post("https://localhost:3001/api/result/save-results", resultPayload);
              if (resultRes.status === 200) {
                  console.log("Saved results successfully");
                  setHasSaved(true);
                  localStorage.setItem(hasSavedKey, 'true');
              }

          } catch (err) {
              console.error("Failed to save quiz result or explanations:", err);
          }
      };

      saveEverything(); // ✅ only call if absolutely needed
    }, [isLogin, userID, quizData, userAnswers, attempts, difficulty, quizID, score, topic, total, hasSaved]);

    return (
      <div className="card bg-dark text-white" style={{ border: '1px solid #237ac3' }}>
            <div className="card-body text-center">
                <h2>Quiz Results</h2>
                <h3>Your Score: {score} out of {total}</h3>
                <p>You got {Math.round((score / total) * 100)}%</p>


                <div className="mt-4">
                    {isLogin && 
                        <>
                            <p>You've attempted this quiz {attempts} times. Would you like to see the explanations or try again?</p>
                            <button className="btn btn-info me-2" onClick={onShowExplanations}>
                                Show Explanations
                            </button>
                        </>
                    }
                    <button className="btn btn-primary me-2" onClick={onTryAgain}>
                        Try Again
                    </button>
                    <button className="btn btn-secondary me-2" onClick={() => navigate(`/quiz-generator/${type}`)}>
                        New Quiz
                    </button>
                    <button className="btn me-2 btn-danger" onClick={() => navigate('/')}>
                        Exit
                    </button>
                </div>
            </div>
        </div>
    );
}

export default QuizResult;
