// src/pages/Explanation.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import QuizExplanations from '../components/QuizExplanations';

const Explanation = () => {
  const { explanationID } = useParams();
  const navigate = useNavigate();
  const [quizData, setQuizData] = useState([]);
  const [userAnswers, setUserAnswers] = useState([]);
  const [loading, setLoading] = useState(true);
  const userID = localStorage.getItem("userID");

  useEffect(() => {
    const fetchExplanationData = async () => {
      try {
        const res = await axios.get(`/api/explanations/$${explanationID}`);
        setQuizData(res.data.questions.quizData);
        setUserAnswers(res.data.questions.userAnswers);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching explanation data:", err);
        setLoading(false);
      }
    };
    fetchExplanationData();
  }, [userID, explanationID]);

  const handleRetake = () => {
    // navigate(`/quiz/${quizID}`); // or wherever your retake route is
  };

  // Use a rendering function approach instead of early returns
  const renderContent = () => {
    if (loading) {
      return <p className="text-white">Loading explanations...</p>;
    }

    if (!quizData.length) {
      return <p className="text-danger">No explanations found.</p>;
    }

    return (
      <div className="container pt-4 pb-4">
        <QuizExplanations
          quizData={quizData}
          userAnswers={userAnswers}
          onRetake={handleRetake}
          navigate={navigate}
          readOnly={true}
        />
      </div>
    );
  };

  return renderContent();
};

export default Explanation;
